$(document).ready(function(){
    $('#twitter,#facebook').each(function() {
        animationHover(this, 'swing');
    });
});

function animationHover(elemento, animacao){
    elemento = $(elemento);
    elemento.hover(
        function() {
            elemento.addClass('animated ' + animacao);
        },
        function(){
            window.setTimeout( function(){
                elemento.removeClass('animated ' + animacao);
            }, 100);
        });
}